"""
Main orchestrator for the Voices of Space project
Combines data ingestion, journal generation, and music creation
"""
import json
import os
from datetime import datetime
from typing import Dict, Any

from data_ingestion import SpaceDataFetcher
from journal_generator import AstronautJournalGenerator
from music_generator import SpaceMusicGenerator

class VoicesOfSpace:
    def __init__(self):
        self.data_fetcher = SpaceDataFetcher()
        self.journal_generator = AstronautJournalGenerator()
        self.music_generator = SpaceMusicGenerator()
        
        # Create output directory
        self.output_dir = "generated_entries"
        os.makedirs(self.output_dir, exist_ok=True)
    
    def create_daily_entry(self) -> Dict[str, Any]:
        """Create a complete daily space journal entry with music"""
        
        print("🚀 Fetching space data...")
        space_data = self.data_fetcher.get_complete_space_data()
        
        print("📝 Generating journal entry...")
        journal_entry = self.journal_generator.generate_journal_entry(space_data)
        
        print("🎵 Creating space music...")
        music_data = self.music_generator.generate_space_music(space_data)
        
        # Combine everything into a complete entry
        complete_entry = {
            'metadata': {
                'date': datetime.now().strftime('%Y-%m-%d'),
                'timestamp': datetime.now().isoformat(),
                'iss_location': space_data['iss_location'],
                'telemetry': space_data['telemetry']
            },
            'journal': {
                'title': journal_entry['title'],
                'entry': journal_entry['journal_entry'],
                'generated_at': journal_entry['generated_at']
            },
            'music': {
                'filename': music_data['filename'],
                'filepath': music_data['filepath'],
                'description': music_data['description'],
                'musical_params': music_data['musical_params'],
                'generated_at': music_data['generated_at']
            },
            'raw_data': space_data
        }
        
        # Save entry to file
        entry_filename = f"entry_{datetime.now().strftime('%Y%m%d_%H%M%S')}.json"
        entry_filepath = os.path.join(self.output_dir, entry_filename)
        
        with open(entry_filepath, 'w', encoding='utf-8') as f:
            json.dump(complete_entry, f, indent=2, ensure_ascii=False)
        
        print(f"✨ Entry saved to: {entry_filepath}")
        
        return complete_entry
    
